    &:not([DISABLED]):hover, &:not([DISABLED]):active, &:not([DISABLED]):focus{
        background: @item-hover-bg;
    }

    &:focus, &:active, &:hover {
        outline: none;
    }

    &.disabled, &[disabled], &.disabled:hover {
        cursor: not-allowed;
        opacity: @disabled-opacity-base;
    }
}

